body.yt-cinema-playing{overflow:hidden}.yt-cinema-greyout{background:rgba(0,0,0,0.75);width:100%;height:100%;z-index:9999999;position:fixed;transition-duration:0.25s}.yt-cinema-container{position:fixed;z-index:9999999;background:black;transform:translate(-50%,-50%);width:calc(60vw + 160px);height:calc((60vw + 160px) / 16 * 9);top:50%;left:50%;box-shadow:0 5px 30px rgba(0,0,0,0.5);transition-duration:0.25s;font-size:14px}.yt-cinema-container iframe{height:100%;width:100%}.yt-cinema-close{float:right;color:white;font-size:30px;margin:32px;line-height:24px;font-weight:bold;font-family:arial;width:24px;text-align:center;cursor:pointer;transition-duration:0.25s}.yt-cinema-close:hover{text-shadow:0 0 10px white}

